pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";

import "./ICampaign.sol";
import "./LinkedToOptIn.sol";
import "./LinkedToOracle.sol";


contract Campaign is Ownable, ICampaign, LinkedToOptIn, LinkedToOracle {

  /* Time corresponding of the start of the campaign */
  uint32 public startCampaignTime;

  /* Time corresponding of the end of the campaign */
  uint32 public endCampaignTime;

  /* Time corresponding of the start of the reward window for the campaign */
  uint32 public startRewardTime;

  /* Time corresponding of the end of the reward window for the campaign */
  uint32 public endRewardTime;

  /* Initial allocation to the campaign */
  uint128 public initialAllocation;

  /* Amount of the reward */
  uint80 public rewardAmount;

  /* Type of the campaign */
  uint8 public campaignType;

  /* Maximum number of trigger per customer */
  uint8 public customerMaxNumberOfTrigger;

  /* IPFS hash of the campaign metadata */
  bytes32 public metadataHash;

  /* IPFS hash of the campaign Image */
  bytes32 public imageHash;

  /* IPFS hash of the CGV */
  bytes32 public tcHash;

  /* Tracking of the number of reward given to an account */
  mapping(address => uint8) private triggers;

  /* Tracks number of times the campaign has rewarded someone */
  uint32 public numberOfTriggers;

  /* Fired each time the campaign rewards a shopper */
  event RewardSent(
    address indexed _rewarded,
    address _campaignAddress,
    uint80 _rewardAmount,
    bytes32 _pob
  );

  /* Fired when the campaign is emptied because it is over */
  event CampaignEmptied(address _campaignAddress);

  /* Fired when the campaign is killed by owner */
  event CampaignKilledByOwner(address _campaignAddress);

  /**
    * @dev Creates a campaign
    * @param _optInRegistryAddress Address of the opt in registry
    * @param _proxyOracleAddress Address of the proxy oracle registry
    * @param _startCampaignTime Time of the start of the campaign
    * @param _endCampaignTime Time of the end of the campaign
    * @param _startRewardTime Time of the start of the reward window for the campaign
    * @param _endRewardTime Time of the end of the reward window for the campaign
    * @param _rewardAmount Amount of the reward
    * @param _campaignType Type of the campaign
    * @param _customerMaxNumberOfTrigger Maximum number of trigger per customer
    * @param _metadataHash IPFS hash of the metadata
    * @param _imageHash IPFS hash of the image
    * @param _tcHash IPFS hash of the cgv
    */
  constructor(
    address _optInRegistryAddress,
    address _proxyOracleAddress,
    uint32 _startCampaignTime,
    uint32 _endCampaignTime,
    uint32 _startRewardTime,
    uint32 _endRewardTime,
    uint80 _rewardAmount,
    uint8 _campaignType,
    uint8 _customerMaxNumberOfTrigger,
    bytes32 _metadataHash,
    bytes32 _imageHash,
    bytes32 _tcHash
  )
    LinkedToOptIn(_optInRegistryAddress)
    LinkedToOracle(_proxyOracleAddress)
    public
    payable
  {
    // Possible requires
    require(_startCampaignTime <= _startRewardTime, "Campaign starts after the start of the reward period");
    require(_startRewardTime <= _endRewardTime, "End is before start.");
    require(_endRewardTime <= _endCampaignTime, "Campaign finishes before the end of the reward period.");
    require(0 < _rewardAmount, "Reward can not be 0.");
    require(_rewardAmount <= msg.value, "There must be at least enough for one reward.");
    require(0 < _customerMaxNumberOfTrigger, "The maximum number of trigger can not be 0.");

    // Initializes the state variables
    startCampaignTime = _startCampaignTime;
    endCampaignTime = _endCampaignTime;
    startRewardTime = _startRewardTime;
    endRewardTime = _endRewardTime;
    initialAllocation = uint128(msg.value);
    rewardAmount = _rewardAmount;
    campaignType = _campaignType;
    customerMaxNumberOfTrigger = _customerMaxNumberOfTrigger;
    metadataHash = _metadataHash;
    imageHash = _imageHash;
    tcHash = _tcHash;
  }

  /**
    * @dev Checks whether or not the campaign is active
    */
  function isActive()
    public
    view
    returns (bool)
  {
    return getTime() >= startRewardTime && getTime() <= endRewardTime;
  }

  /**
    * @dev Checks whether or not the campaign is over
    */
  function isOver()
    public
    view
    returns (bool)
  {
    return getTime() > endCampaignTime;
  }

  /**
    * @dev Gets the campaign type
    */
  function getCampaignType()
    public
    view
    returns (uint8)
  {
    return campaignType;
  }

  /**
    * @dev Rewards an user with the rewardAmount
    * @param _account Address of the rewarded user
    * @param _pob POB sent by the oracle
    */
  function reward(
    address _account,
    bytes32 _pob
  )
    public
    onlyFromOracle
    returns (bool)
  {
    // The campaign should be active
    if (isActive()) {
      // Contract must have at least rewardAmount
      require(address(this).balance >= rewardAmount, "Not enough funds");

      // The account should be eligible to reward
      require(isEligible(_account, this), "User not eligible to reward");

      // The _account should have less triggers than the maximum allowed
      require(triggers[_account] < customerMaxNumberOfTrigger, "Too many triggers");

      // Increment the trigger
      ++triggers[_account];
      numberOfTriggers++;

      // Transfer the rewardAmount
      _account.transfer(rewardAmount);
      emit RewardSent(_account, address(this), rewardAmount, _pob);

      return true;
    }

    // if the campaign is over the smart contract is emptied
    if (isOver()) {
      require(_emptyCampaign(), "Error while return campaign funds to owner");
      return true;
    }
  }

  /**
    * @dev Update the ipfs hash of the metadata
    * @param _metadataHash The new hash of the metadata
    */
  function updateMetadataHash(
    bytes32 _metadataHash
  )
    public
    onlyOwner
    returns (bool)
  {
    metadataHash = _metadataHash;
    return true;
  }

  /**
    * @dev Update the ipfs hash of the image
    * @param _newImageHash The new hash of the image
    */
  function updateImageHash(
    bytes32 _newImageHash
  )
    public
    onlyOwner
    returns (bool)
  {
    imageHash = _newImageHash;
    return true;
  }

  /**
    * @dev Kill the smart-contract of the campaign
    */
  function killCampaign()
    public
    onlyOwner
  {
    emit CampaignKilledByOwner(address(this));
    selfdestruct(owner());
  }

  /**
    * @dev Kill the smart-contract of the campaign
    */
  function _emptyCampaign()
    internal
    returns(bool)
  {
    // Selfdestruct the contract and send the residual amount to the contract owner
    owner().transfer(address(this).balance);
    emit CampaignEmptied(address(this));
    return true;
  }

  /**
    * @dev Returns current time
    */
  function getTime() public view returns (uint32) {
    return uint32(block.timestamp);
  }

  /**
    * @dev Gets succinct information about the campaign
    */
  function getCampaignPrimaryInfo()
    public
    view
    returns (bytes32, bool, uint80, uint8)
  {
    return (
      metadataHash,
      isActive(),
      rewardAmount,
      campaignType
    );
  }

  /**
    * @dev Gets remaining information about the campaign
    */
  function getCampaignSecondaryInfo()
    public
    view
    returns (bytes32, bytes32, uint32, uint32, uint32, uint32, uint8, uint32, uint8, uint128)
  {
    return (
      imageHash,
      tcHash,
      startCampaignTime,
      endCampaignTime,
      startRewardTime,
      endRewardTime,
      customerMaxNumberOfTrigger,
      numberOfTriggers,
      triggers[msg.sender],
      initialAllocation
    );
  }
}

