pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";


contract CampaignRegistry is Ownable {

  /* Stores whether a campaign has been registered */
  struct CampaignDetails {
    uint32 index;
    bool isRegistered;
  }

  /* Array of all campaigns in registry */
  address[] public campaigns;
  mapping(address => CampaignDetails) public allCampaignsDetails;

  event CampaignRegistered(address _campaignAddress, uint32 _campaignIndex);
  event CampaignRemoved(address _campaignAddress, uint32 _campaignIndex);

  /**
   * @dev Checks if a campaign is in a registry
   * @param _campaignAddress Address of the campaign contract
   */
  function isRegistered(address _campaignAddress) public view returns(bool) {
    return allCampaignsDetails[_campaignAddress].isRegistered;
  }

  /**
   * @dev Returns all campaigns in the registry
   */
  function getCampaigns()
    public
    view
    returns(address[])
  {
    return campaigns;
  }

  /**
   * @dev Gets campaign Index
   * @param _campaignAddress Address of the campaign contract
   */
  function getIndex(address _campaignAddress)
    public
    view
    returns(uint32)
  {
    return allCampaignsDetails[_campaignAddress].index;
  }

  /**
   * @dev Adds campaign to registry
   * @param _campaignToRegister Address of the campaign contract
   */
  function registerCampaign(address _campaignToRegister)
    public
    onlyOwner
    returns(uint32)
  {
    require(!isRegistered(_campaignToRegister));

    allCampaignsDetails[_campaignToRegister].isRegistered = true;
    allCampaignsDetails[_campaignToRegister].index = uint32(campaigns.push(_campaignToRegister) - 1);

    emit CampaignRegistered(_campaignToRegister, allCampaignsDetails[_campaignToRegister].index);
    return allCampaignsDetails[_campaignToRegister].index;
  }

  /**
   * @dev Deletes campaign from registry
   * @param _campaignToRemove Address of the campaign contract
   */
  function removeCampaign(address _campaignToRemove)
    public
    onlyOwner
    returns(uint32)
  {
    require(isRegistered(_campaignToRemove));
    allCampaignsDetails[_campaignToRemove].isRegistered = false;

    uint32 rowToRemove = allCampaignsDetails[_campaignToRemove].index;
    address keyToMove = campaigns[campaigns.length-1];

    // Switching element to remove with the last element in the array
    campaigns[rowToRemove] = keyToMove;
    allCampaignsDetails[keyToMove].index = rowToRemove;

    // Removal of last element in campaignIndex, which
    // otherwise would be present twice in the list
    campaigns.length--;

    emit CampaignRemoved(_campaignToRemove, rowToRemove);

    return rowToRemove;
  }

  function kill()
    public
    onlyOwner
  {
    selfdestruct(owner());
  }
}
