pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";

import "./OptInRegistry.sol";


contract LinkedToOptIn is Ownable {

  /* Address of the optInRegistry */
  address private optInRegistryAddress;

  /**
   * @dev Registers the address of the opt in registry
   * @param _optInRegistryAddress Address of the opt in registry
   */
  constructor(
    address _optInRegistryAddress
  )
    internal
  {
    // Set the optInRegistryAddress
    optInRegistryAddress = _optInRegistryAddress;
  }

  /**
   * @dev Verifies if an address has opted in for the campaign
   * @param _account The address of the seeker of reward
   */
  function isEligible(
    address _account,
    address _campaign
  )
    internal
    view
    returns (bool)
  {
    OptInRegistry optInRegistry = OptInRegistry(optInRegistryAddress);
    return optInRegistry.hasOptedIn(_account, _campaign);
  }
}
