pragma solidity ^0.4.24;

import "./Campaign.sol";


contract MockCampaign is Campaign {

  /* Fake Time of the contract */
  uint32 fakeTime;

  /**
    * @dev Creates a campaign and initializes the fakeTime
    * @param _optInRegistryAddress Address of the opt in registry
    * @param _proxyOracleAddress Address of the proxy oracle registry
    * @param _startCampaignTime Time of the start of the campaign
    * @param _endCampaignTime Time of the end of the campaign
    * @param _startRewardTime Time of the start of the reward window for the campaign
    * @param _endRewardTime Time of the end of the reward window for the campaign
    * @param _rewardAmount Amount of the reward
    * @param _campaignType Type of the campaign
    * @param _customerMaxNumberOfTrigger Maximum number of trigger per customer
    * @param _metadataHash IPFS hash of the metadata
    * @param _imageHash IPFS hash of the image
    * @param _cgvHash IPFS hash of the cgv
    */
  constructor(
    address _optInRegistryAddress,
    address _proxyOracleAddress,
    uint32 _startCampaignTime,
    uint32 _endCampaignTime,
    uint32 _startRewardTime,
    uint32 _endRewardTime,
    uint80 _rewardAmount,
    uint8 _campaignType,
    uint8 _customerMaxNumberOfTrigger,
    bytes32 _metadataHash,
    bytes32 _imageHash,
    bytes32 _cgvHash
  )
    Campaign(
      _optInRegistryAddress,
      _proxyOracleAddress,
      _startCampaignTime,
      _endCampaignTime,
      _startRewardTime,
      _endRewardTime,
      _rewardAmount,
      _campaignType,
      _customerMaxNumberOfTrigger,
      _metadataHash,
      _imageHash,
      _cgvHash
    )
    public
    payable
  {
    fakeTime = uint32(block.timestamp);
  }

  /**
    * @dev Gets the fakeTime
    */
  function getTime() public view returns (uint32) {
    return fakeTime;
  }

  /**
    * @dev set the fake time of the contract
    * @param _fakeTime The fake time to be set
    */
  function setTime(uint32 _fakeTime) public returns (bool) {
    fakeTime = _fakeTime;

    return true;
  }
}

