pragma solidity ^0.4.24;


contract OptInRegistry {

  /* Stores the status of an opt-in for a given user address and campaign address */
  struct OptinStruct {
    bool hasOptedIn;
    uint24 index;
  }

  /* Stores all the opt-ins of a given user, and a list of all the corresponding campaign addresses */
  struct UserOptins {
    address[] campaigns;
    mapping(address => OptinStruct) optinStructs;
  }

  /* Stores all the opt-ins for all users */
  mapping(address => UserOptins) private allUsersOptins;

  /* Stores the number of people currently opted into this campaign. Decreases with each opt-out. */
  mapping(address => uint32) public campaignOptInCounts;

  event OptedIn(address indexed userAddress, address indexed campaignAddress, uint24 campaignIndex);
  event OptedOut(address indexed userAddress, address indexed campaignAddress, uint24 campaignIndex);

  /**
    * @dev Checks that the user sending the transaction is already known
    */
  modifier knownUser() {
    require(allUsersOptins[msg.sender].campaigns.length != 0, "Unknown user !");
    _;
  }

  /**
    * @dev Checks that the transaction is sent by either the user's account or the campaign contract
    */
  modifier requestedByUserOrCampaign(address _userAddress, address _campaignAddress) {
    require((msg.sender == _userAddress) || (msg.sender == _campaignAddress), "The sender needs to be either the user or the campaign");
    _;
  }

  /**
    * @dev Checks whether a user has opted into a campaign
    * @param _userAddress Address of the user account
    * @param _campaignAddress Address of the campaign contract
    */
  function hasOptedIn(
    address _userAddress,
    address _campaignAddress
  )
    public
    view
    requestedByUserOrCampaign(_userAddress, _campaignAddress)
    returns(bool)
  {
    return allUsersOptins[_userAddress].optinStructs[_campaignAddress].hasOptedIn;
  }

  /**
    * @dev Records a user's opt-in to a campaign
    * @param _campaignAddress Address of the campaign contract
    */
  function optInToCampaign(
    address _campaignAddress)
    public
    returns(uint24)
  {
    require(!hasOptedIn(msg.sender, _campaignAddress), "The user is already opted in to this campaign.");

    UserOptins storage userOptins = allUsersOptins[msg.sender];
    userOptins.optinStructs[_campaignAddress].hasOptedIn = true;

    // Simultaneously add element in campaigns and set the index in the OptinStruct
    userOptins.optinStructs[_campaignAddress].index = uint24(userOptins.campaigns.push(_campaignAddress)-1);
    campaignOptInCounts[_campaignAddress]++;
    emit OptedIn(
      msg.sender,
      _campaignAddress,
      userOptins.optinStructs[_campaignAddress].index);
    return uint24(userOptins.campaigns.length-1);
  }

  /**
    * @dev Records a user's opt-out from a campaign, which is equivalent to removing the previously given opt-in
    * @param _campaignAddress Address of the campaign contract
    */
  function optOutToCampaign(
    address _campaignAddress
  )
    public
    returns(uint24)
  {
    require(hasOptedIn(msg.sender, _campaignAddress), "The user did not opt in, they cannot opt out.");

    address[] storage campaignsForThisUser = allUsersOptins[msg.sender].campaigns;

    // Set the boolean back to false before removing the campaign from campaigns
    // Otherwise if directly access the struct hasOptedIn reads True
    allUsersOptins[msg.sender].optinStructs[_campaignAddress].hasOptedIn = false;

    // Defining the index of element to delete and of last element
    uint24 rowToDelete = allUsersOptins[msg.sender].optinStructs[_campaignAddress].index;
    address keyToMove = campaignsForThisUser[campaignsForThisUser.length-1];

    // Switching element to delete with the last element in the array
    campaignsForThisUser[rowToDelete] = keyToMove;
    allUsersOptins[msg.sender].optinStructs[keyToMove].index = rowToDelete;

    // Deletion of last element in campaigns, which
    // otherwise would be present twice in the list
    campaignsForThisUser.length--;
    campaignOptInCounts[_campaignAddress]--;

    emit OptedOut(
      msg.sender,
      _campaignAddress,
      rowToDelete);

    return rowToDelete;
  }

  /**
    * @dev Returns all campaigns for which a user has given an opt-in
    */
  function getUserCampaigns()
    public
    view
    knownUser
    returns(address[])
  {
    return allUsersOptins[msg.sender].campaigns;
  }

  // Not RequestedFromUser because it might be good for surveying users' activity
  /**
    * @dev Returns the number of opt-ins a user has given
    * @param _userAddress Address of the user account
    */
  function getUserOptinCount(
    address _userAddress
  )
    public
    view
    returns(uint24)
  {
    return uint24(allUsersOptins[_userAddress].campaigns.length);
  }

  function getCampaignOptinCount(
    address _campaignAddress
  )
    public
    view
    returns(uint32)
  {
    return campaignOptInCounts[_campaignAddress];
  }
}

